import { Button } from "@/components/ui/button"
import {
    Card,
    CardContent,
    
    CardFooter,
    CardHeader,
    CardTitle,
  } from "@/components/ui/card"
  import { Input } from "@/components/ui/input"
  import { Label } from "@/components/ui/label"
import { Avatar, AvatarImage, AvatarFallback } from "@radix-ui/react-avatar"
import {  useNavigate } from 'react-router-dom';
import { SetStateAction, useState } from "react"

interface FormData {
  email: string;
  password: string;
}

function Login() {
  const [selectedAvatar, setSelectedAvatar] = useState('');
  const handleAvatarClick = (avatarType: SetStateAction<string>) => {
    setSelectedAvatar(avatarType);
  };
  
  const navigate = useNavigate();
  const [formData, setFormData] = useState<FormData>({
   
    email: '',
    password: '',
  });
  const [errors, setError] = useState<Record<string, string>>({});

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    // Validate username
    
    if (!selectedAvatar) {
      setError({ message: 'Please fill in all fields, including selecting an avatar.' });
      return;
    }
    
    // Validate email
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Invalid email address';
    }

    // Validate password
    if (!formData.password.trim()) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters long';
    }

    setError(newErrors);
    return Object.keys(newErrors).length === 0; // Return true if there are no errors
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { id, value } = event.target;
    setFormData({
      ...formData,
      [id]: value,
    });
    // Clear validation error when the user starts typing in the field
    setError((prevErrors) => {
      const newErrors = { ...prevErrors };
      newErrors[id] = '';
      return newErrors;
    });
  };

  const handleSubmit = () => {
    
    // Validate form before submission
    if (validateForm()) {
    
      navigate('/testing', { replace: true });
    }
  };


  return (
    <div className="flex items-center justify-center h-screen">
    <Card className="w-[350px] gap-4">
      
  <CardHeader>
    <CardTitle>Login</CardTitle>
   
  </CardHeader>
  <CardContent>
  <div className='flex gap-12 items-center justify-center'>
    
            <Avatar 
              onClick={() => handleAvatarClick('developer')}
              style={{
                border: selectedAvatar === 'developer' ? '2px solid #007BFF' : 'none',
              }}
            >
              <AvatarImage src="data:image/jpeg;base64," alt="Avatar 1" style={{ width: '50px', height: '50px' }} />
              <AvatarFallback></AvatarFallback>
              <span style={{ marginLeft: '5px', fontWeight: 'italic', color: 'white', fontSize: '12px' }}>Dev</span>
            </Avatar>
            <Avatar
              onClick={() => handleAvatarClick('agency')}
              style={{
                border: selectedAvatar === 'agency' ? '2px solid #007BFF' : 'none',
              }}
            >
              <AvatarImage src="data:image/jpeg;base64," alt="Avatar 2" style={{ width: '50px', height: '50px' }} />
              <AvatarFallback></AvatarFallback>
              <span style={{ marginLeft: '5px', fontWeight: 'italic', color: 'white', fontSize: '12px' }}>Agency</span>
            </Avatar>
            
          </div><br/>
          <div className='grid gap-2 items-start space-y-2'>
            <Label htmlFor="email">Email</Label>
            <Input
              type="email"
              placeholder="Enter email"
              id="email"
              value={formData.email}
              onChange={handleChange}
            />
            {errors.email && <span className="text-red-500">{errors.email}</span>}
          </div><br/>
          <div className='grid gap-2 items-start space-y-2'>
            <Label htmlFor="password">Password</Label>
            <Input
              type="password"
              placeholder="Enter Password"
              id="password"
              value={formData.password}
              onChange={handleChange}
            />
            {errors.password && <span className="text-red-500">{errors.password}</span>}
          </div>
         
        </CardContent>
        <CardFooter>
          <Button className="w-full" onClick={handleSubmit}>Submit</Button>
        </CardFooter>
</Card></div>

  )
}

export default Login
